const ime=document.querySelector(".fancy"),strIme=ime.textContent,splitText=strIme.split("");ime.textContent="";for(let t=0;t<splitText.length;t++)ime.innerHTML+="<span>"+splitText[t]+"</span>";let char=0,timer=setInterval(onTick,90);function onTick(){const t=ime.querySelectorAll("span")[char];t.classList.add("fancyColors"),++char===splitText.length&&complete()}function complete(){clearInterval(timer),timer=null}